INSERT INTO `pf_projects` (`id`, `name`, `description`, `content`, `place`, `client`, `is_featured`, `image`, `images`, `views`, `status`, `created_at`, `updated_at`, `author`, `start_date`, `order`) VALUES
	(1, 'KP Commerce', 'Multi-vendor eCommerce platform with AI analytics\r\nMulti-vendor support, inventory management\r\nOrder processing, AI-powered analytics\r\nTarget Market: Digital and physical product sellers', '<p>A scalable eCommerce SaaS solution with support for both physical and digital products. Offers multi‑vendor sales, inventory control, order management, and AI analytics.<br>You led development using Laravel, MySQL, and Vue.js. Built vendor modules, product catalog interfaces, shopping flows, AI-powered sales insights, and integrated payment gateways and tax rules. Backend optimized for high throughput and multi-tenancy.</p>', NULL, 'Corporate Client', 1, 'code/projects/kpcommerce.png', NULL, 5790, 'published', '2025-06-25 17:23:18', '2025-08-07 03:24:28', NULL, '2025-06-03', 0),
	(2, 'KP Learn', 'Online course marketplace with booking system\r\nInstructor dashboard, video lesson uploads\r\nRole-based access, sales analytics\r\nTarget Market: Training institutes, educators, course creators', '<p>An online course marketplace and LMS similar to Udemy, allowing training institutes and educators to sell course content. Features instructor dashboards, video lessons, student analytics, and booking/payment support.<br>You developed both Flutter-based student mobile apps and Laravel backend with integrated Firebase. You implemented video streaming, role-based access, sales reports, scheduling, and communication tools.</p>', NULL, 'Education Startup', 0, 'code/projects/kplearn.png', NULL, 8832, 'published', '2025-06-25 17:23:18', '2025-08-07 03:28:20', NULL, '2025-05-29', 0),
	(4, 'KP Space', 'Multipurpose agency platform with website builder and eCommerce integration\r\nResponsive website builder, portfolio management\r\nSEO optimization, analytics dashboard\r\nTarget Market: Agencies, startups, businesses', '<p>A multipurpose agency platform combining website builders, portfolio tools, and SEO analytics. Designed for startups, agencies, and small businesses to manage online presence and performance.<br>You headed the full-stack development: Laravel backend, responsive UI with Vue.js and Tailwind, drag‑and‑drop portfolio builder, SEO optimization dashboards, and real‑time visitor analytics. You enabled template support and streamlined publishing workflows.</p>', NULL, 'Kamal Paterson LTD', 1, 'code/projects/kpspace.png', NULL, 5792, 'published', '2025-06-25 17:23:18', '2025-08-07 03:30:03', NULL, '2023-04-26', 0),
	(5, 'KP HRM', 'Human resource and payroll management system\r\nEmployee records, payroll processing\r\nSecure authentication, reporting and analytics\r\nTarget Market: HR departments, organizations', '<p>A comprehensive human resource and payroll management platform including employee profiles, attendance, automated payroll processing, secure login, and reporting.<br>You architected the Laravel backend with robust authentication and data encryption, designed the Vue.js admin panel, and built integration points for analytics dashboards. You also supported API integration with other HR tools and mobile apps.</p>', NULL, 'Kamal Paterson LTD', 1, 'code/projects/kphrm.png', NULL, 0, 'published', '2025-08-07 00:43:25', '2025-08-07 03:29:57', NULL, NULL, 0),
	(6, 'KP Task', 'Project management and team collaboration platform\r\nKanban board, calendar view, team management\r\nProject dashboards, time tracking, activity logs\r\nTarget Market: Project teams, organizations', '<p>A team collaboration and project management tool featuring Kanban boards, calendars, time tracking, activity logs, and team dashboards. Built within your SaaS ecosystem for organizational productivity.<br>You developed the Flutter mobile app and Vue.js web interface, backend in Laravel/MySQL, and included real‑time updates with Firebase. You led UI/UX design, cross-platform consistency, role-based access, and integration with KP HRM and analytics modules.</p>', NULL, NULL, 1, 'code/projects/kptask.png', NULL, 0, 'published', '2025-08-07 00:43:57', '2025-08-07 03:29:52', NULL, '2025-04-09', 0),
	(7, 'KP Cards', 'Digital business card builder and portfolio manager\r\nDigital card creation, portfolio management\r\nResponsive design, secure sharing options\r\nTarget Market: Professionals, businesses', '<p>A digital business card and portfolio builder within the SaaS ecosystem you built at Kamal‑Paterson Ltd. Users can design cards, manage contact info, and share securely via QR or NFC.<br>Your responsibilities included designing the card builder UI, secure card storage, sharing mechanisms (NFC/URL embed), and analytics dashboards. Backend via Laravel interfaces with Vue.js UI. This platform enhanced networking efficiency for professionals.</p>', NULL, 'Kamal Paterson LTD', 1, 'code/projects/kpcards.png', NULL, 0, 'published', '2025-08-07 00:44:32', '2025-08-07 03:29:45', NULL, '2025-03-11', 0),
	(8, 'Porua', 'An eBook app for Bangla literature, featuring a user-friendly interface, offline reading, and adjustable fonts.\r\nExtensive Bangla eBook library for all ages and tastes\r\nUser-friendly navigation and adjustable font size\r\nOffline access to downloaded eBooks', '<p>An immersive Bangla eBook reader app featuring a vast library of stories, poems, and novels. Built to offer smooth navigation, adjustable font sizes, and offline reading—especially for new digital readers <a class="flex h-4.5 overflow-hidden rounded-xl px-2 text-[9px] font-medium text-token-text-secondary! bg-[#F4F4F4]! dark:bg-[#303030]! transition-colors duration-150 ease-in-out" href="https://play.google.com/store/apps/details?hl=en-US&amp;id=org.porua.app&amp;utm_source=chatgpt.com" target="_blank" rel="noopener noreferrer"><span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between overflow-hidden grow truncate text-center">Google Play</span> </a><a class="flex h-4.5 overflow-hidden rounded-xl px-2 text-[9px] font-medium text-token-text-secondary! bg-[#F4F4F4]! dark:bg-[#303030]! transition-colors duration-150 ease-in-out" href="https://www.appbrain.com/app/porua-bangla-ebook-reader/org.porua.app?utm_source=chatgpt.com" target="_blank" rel="noopener noreferrer"><span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between overflow-hidden grow truncate text-center">AppBrain</span> </a><a class="flex h-4.5 overflow-hidden rounded-xl px-2 text-[9px] font-medium text-token-text-secondary! bg-[#F4F4F4]! dark:bg-[#303030]! transition-colors duration-150 ease-in-out" href="https://apps.apple.com/us/app/porua-bangla-ebook-reader/id6450507481?utm_source=chatgpt.com" target="_blank" rel="noopener noreferrer"><span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between overflow-hidden grow truncate text-center">Apple</span> </a>.<br>You architected this using Flutter for the front end and Laravel for the backend, creating features like scalable content delivery, bookmarking, reading statistics, and customization for low-bandwidth environments. You delivered attention‑optimized UI using Figma and improved accessibility with adjustable themes.</p>', NULL, 'Technosoft Informatics LTD', 1, 'code/projects/porua.png', NULL, 0, 'published', '2025-08-07 00:44:56', '2025-08-07 03:29:40', NULL, NULL, 0),
	(9, 'Sleepy Owl Stories', 'AI-powered personalized bedtime stories for children. Create custom stories, soothing narration, and magical illustrations for better sleep.', '<p>A bedtime storytelling app that crafts personalized AI-generated tales starring children, paired with soothing narration, calming music, and illustrations to improve sleep quality and engagement <a class="flex h-4.5 overflow-hidden rounded-xl px-2 text-[9px] font-medium text-token-text-secondary! bg-[#F4F4F4]! dark:bg-[#303030]! transition-colors duration-150 ease-in-out" href="https://sleepy-owl-stories.com/?utm_source=chatgpt.com" target="_blank" rel="noopener noreferrer"><span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between overflow-hidden grow truncate text-center">Sleepy Owl Stories</span> </a><a class="flex h-4.5 overflow-hidden rounded-xl px-2 text-[9px] font-medium text-token-text-secondary! bg-[#F4F4F4]! dark:bg-[#303030]! transition-colors duration-150 ease-in-out" href="https://alternativeto.net/software/sleepy-owl-stories/about/?utm_source=chatgpt.com" target="_blank" rel="noopener noreferrer"><span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between overflow-hidden grow truncate text-center">AlternativeTo</span> </a><a class="flex h-4.5 overflow-hidden rounded-xl px-2 text-[9px] font-medium text-token-text-secondary! bg-[#F4F4F4]! dark:bg-[#303030]! transition-colors duration-150 ease-in-out" href="https://play.google.com/store/apps/details?hl=en&amp;id=com.kpcloud.bedtime_app&amp;utm_source=chatgpt.com" target="_blank" rel="noopener noreferrer"><span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between overflow-hidden grow truncate text-center">Google Play</span> </a>.<br>You led end-to-end development: from gathering requirements to launching a Flutter application featuring GPT‑based story generation, text‑to‑speech voices, illustration blending, and multilingual support. You also designed a responsive admin dashboard for story templates and content moderation.</p>', NULL, 'Kamal Paterson LTD', 1, 'code/projects/sos.png', NULL, 0, 'published', '2025-08-07 00:45:40', '2025-08-07 03:29:34', NULL, NULL, 0),
	(10, 'Pocket Chef AI', 'AI cooking assistant with ingredient recognition, recipe generation, and meal planning', '<p>An AI-powered cooking assistant that recommends recipes based on available ingredients, dietary preferences, and meal planning needs In your technical architect role, you designed and implemented the Flutter-based front end with AI/ML integration for GPT-based recipe generation, custom meal planners, grocery lists, and offline recipe access. You deployed AWS-hosted infrastructure, CI/CD pipelines, and optimized performance for fast, on‑device response.</p>', NULL, 'Kamal Paterson LTD', 1, 'code/projects/pocketchefai.png', NULL, 0, 'published', '2025-08-07 00:46:58', '2025-08-07 03:29:27', NULL, '2025-07-14', 0),
	(11, 'OnlyMCQ', 'Educational MCQ platform for exam preparation', '<p>A competitive exam preparation platform offering thousands of MCQs across public/private sector tests with real-time feedback, analytics, and custom difficulty settings <a class="flex h-4.5 overflow-hidden rounded-xl px-2 text-[9px] font-medium text-token-text-secondary! bg-[#F4F4F4]! dark:bg-[#303030]! transition-colors duration-150 ease-in-out" href="https://play.google.com/store/apps/details?hl=en&amp;id=com.onlymcq.app&amp;utm_source=chatgpt.com" target="_blank" rel="noopener noreferrer"><span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between grow truncate overflow-hidden text-center">Google Play</span> <span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between -me-1 rounded-full px-1 text-[#8F8F8F]">+1</span> </a><a class="flex h-4.5 overflow-hidden rounded-xl px-2 text-[9px] font-medium text-token-text-secondary! bg-[#F4F4F4]! dark:bg-[#303030]! transition-colors duration-150 ease-in-out" href="https://appadvice.com/app/only-mcq/6476809563?utm_source=chatgpt.com" target="_blank" rel="noopener noreferrer"><span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between overflow-hidden grow truncate text-center">AppAdvice</span> </a>.<br>You built the app using Flutter and Firebase to support real-time analytics (question accuracy, time per question, difficulty levels) and adaptive learning paths. Features include instant feedback, progress tracking, subject-wise performance dashboards, and integration of video/PDF lessons via React Native/WebViews.</p>', NULL, 'Technosoft Informatics LTD', 1, 'code/projects/onlymcq.png', NULL, 0, 'published', '2025-08-07 00:47:50', '2025-08-07 03:29:21', NULL, '2024-06-04', 0),
	(12, 'Saifurs Books', 'Digital educational content platform', '<p>A digital educational content platform supporting <i>Saifur’s Pvt. Ltd.</i>, a renowned Bangladeshi education brand led by author Mr. Saifur Rahman Khan. The app delivers a wide selection of books—especially IELTS, writing, grammar, and career preparation volumes—online/offline with fast delivery from saifursbooks.com <span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between overflow-hidden grow truncate text-center">Google Play</span> <a class="flex h-4.5 overflow-hidden rounded-xl px-2 text-[9px] font-medium text-token-text-secondary! bg-[#F4F4F4]! dark:bg-[#303030]! transition-colors duration-150 ease-in-out" href="https://www.saifursbooks.com/?utm_source=chatgpt.com" target="_blank" rel="noopener noreferrer"><span class="ms-1 inline-flex max-w-full items-center relative top-[-0.094rem] animate-[show_150ms_ease-in] start-0 bottom-0 flex h-full w-full h-4 justify-between overflow-hidden grow truncate text-center">Saifurs Books</span> </a>.<br>As lead developer, you engineered a responsive cross-platform Flutter app fused with a Laravel backend for seamless browsing, secure purchases, eBook reader integration, and offline access. You implemented Figma-based UI/UX design, secure authentication, and AI-based recommendation features, transforming a traditional bookstore into a modern digital educational platform.</p>', NULL, 'Technosoft Informatics LTD', 1, 'code/projects/saifursbooks.png', NULL, 0, 'published', '2025-08-07 00:48:48', '2025-08-07 03:29:14', NULL, '2024-04-30', 0);

